import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import AdminAction from '../components/AdminAction';

export default function UpdateUser() {
    const [formData, setFormData] = useState({
        emailId: '',
    });
    const { currentUser } = useSelector((state) => state.user);
    const [loading, setLoading] = useState(false);
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState(false);
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError(false);
            const res = await fetch('/api/proposal/uploadProposal', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ...formData,
                    email: currentUser.email,
                    name: currentUser.name,
                }),
            });
            const data = await res.json();
            setLoading(false);
            if (data.success === false) {
                setError(data.message);
            }
            window.location.reload(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };
    console.log(formData);
    return (
        <div>
            <AdminAction />
            <>
                <h1 className="text-3xl font-semibold text-center">Delete User</h1>
                <form onSubmit={handleSubmit} className="row g-3 align-items-center">
        <div className="col-md-6">
          <input type="text" id="emailId" name="emailId" onChange={handleChange} placeholder="Enter Email ID" className="form-control ml-50" value={formData.emailId} />
        </div>
        <div className="col-md-6">
          <button disabled={loading || uploading} type="submit" className="btn btn-danger ml-2">
            {loading ? 'Deleting...' : 'Delete User'}
          </button>
        </div>
        {error && <p className="text-danger">{error}</p>}
      </form>
            </>
        </div>
    )
}
