import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import AdminAction from '../components/AdminAction';

export default function UpdateUser() {
    const [formData, setFormData] = useState({
        emailId: '',
    });
    const { currentUser } = useSelector((state) => state.user);
    const [loading, setLoading] = useState(false);
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState(false);
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError(false);
            const res = await fetch('/api/proposal/uploadProposal', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    ...formData,
                    email: currentUser.email,
                    name: currentUser.name,
                }),
            });
            const data = await res.json();
            setLoading(false);
            if (data.success === false) {
                setError(data.message);
            }
            window.location.reload(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };
    console.log(formData);
    return (
        <div>
            <AdminAction />
            <div className="custom-container mt-5">
      <h1 className="custom-heading">Delete User</h1>
      <form onSubmit={handleSubmit} className="custom-form">
        <div className="custom-input-group">
          <label htmlFor="emailId" className="custom-label">Email ID:</label>
          <input type="text" id="emailId" name="emailId" onChange={handleChange} placeholder="Enter Email ID" className="custom-input" value={formData.emailId} />
        </div>
        <div className="custom-button-group">
          <button disabled={loading || uploading} type="submit" className="custom-button">
            {loading ? 'Deleting...' : 'Delete User'}
          </button>
        </div>
        {error && <p className="custom-error-message">{error}</p>}
      </form>
    </div>
        </div>
    )
}
