import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import AdminAction from '../components/AdminAction';

export default function UpdateUser() {
    const [formData, setFormData] = useState({
        emailId: '',
    });
    const {currentUser}=useSelector((state)=>state.user);
    const [loading, setLoading] = useState(false);
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          setLoading(true);
          setError(false);
          const res = await fetch('/api/proposal/uploadProposal', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              ...formData,
              email: currentUser.email,
              name: currentUser.name,
            }),
          });
          const data = await res.json();
          setLoading(false);
          if (data.success === false) {
            setError(data.message);
          }
          window.location.reload(false);
        } catch (error) {
          setError(error.message);
          setLoading(false);
        }
      };
    
    return (
        <div>
            <AdminAction />
            <>
                <h1 className='text-3xl font-semibold text-center'>Delete user</h1>
                <form onSubmit={handleSubmit} className='flex flex-col sm:flex-row gap-4'>
                    <div className='flex flex-col gap-4 flex-2 '>
                        <input type='text' onChange={handleChange} placeholder='Item' id='emailId' className='border p-3 rounded-lg' value={formData.description} />

                    </div>
                    <div className='flex flex-col flex-1 gap-4'>
                        <button disabled={loading || uploading} className='p-3 bg-slate-700 text-white rounded-lg uppercase hover:opacity-95 disabled:opacity-80'>
                            {loading ? 'Creating...' : 'Create listing'}
                        </button>
                        {error && <p className='text-red-700 text-sm'>{error}</p>}
                    </div>
                </form>
            </>
        </div>
    )
}
